#include <stdio.h> int main() { int i, n; int t1 = 0, t2 = 1; int nextTerm = t1 + t2; int sum=1; { printf("Enter the number of terms: "); scanf("%d", &n); printf("Fibonacci Series: %d, %d, ", t1, t2); for (i = 3; nextTerm <=10000; ++i) { printf("%d, ", nextTerm); t1 = t2; t2 = nextTerm; nextTerm = t1 + t2; sum=sum+nextTerm; } if(sum%7==0) { printf("divisible by 7"); } if(sum%3==0) { printf("divisible by 3"); } if(sum%5==0) { printf("divisible by 5"); } return 0; }}
